mapper.select:
SELECT pay_state FROM project_settlement WHERE project_settlement.type_id=3 AND state=2  

	AND project_id=#{project_id}

ORDER BY create_at DESC LIMIT 1

select * from project_early
where project_id=#{project_id}

		and state=3

select * from project_start
where project_id=#{project_id}

		and state=3

select * from project_before_check
where project_id=#{project_id}

		and state=3

select * from project_check c
where c.project_id=#{project_id}

		and c.state=3

select t.type_name name,s.* from project_settlement s 
inner join project_settlement_type t on s.type_id=t.id
where project_id=#{project_id}

		and s.id < #{settlement_id}

order by s.id DESC limit 1

SELECT contract_amount FROM `project_settlement` s WHERE s.type_id=3 and contract_amount is not null
and s.project_id=#{project_id}
LIMIT 1

mapper.update:
update project_early
contract=#{contract}, qualification=#{qualification}, sec_protocol=#{sec_protocol}, start_credential=#{start_credential}, io_credential=#{io_credential}, constructionfee=#{constructionfee}, safety_training=#{safety_training}, people_information=#{people_information}, csd_receipt=#{csd_receipt}, sop_certificate=#{sop_certificate}, is_securitycheck=#{is_securitycheck}, record=#{record}, drawings=#{drawings}, `else`=#{else}, state=#{state}, create_by=#{create_by}
where project_id=#{project_id}

update `project`
name=#{name}, project_type=#{project_type}, location=#{location}, construction_org=#{construction_org}, supervising_org=#{supervising_org}, supervisor=#{supervisor}, supervisor_phone=#{supervisor_phone}, PM=#{PM}, constructor=#{constructor}, PM_phone=#{PM_phone}, is_urgency=#{is_urgency}, sign_file=#{sign_file}, owner_consent=#{owner_consent}, state=#{state}, create_at=#{create_at}, create_by=#{create_by}, save_state=#{save_state}, create_by=#{create_by}
where id=#{id}

update project_start
cpce_site=#{cpce_site}, st_site=#{st_site}, cstd_site=#{cstd_site}, ts_form=#{ts_form}, situation_site=#{situation_site}, material=#{material}, office_site=#{office_site}, drawings=#{drawings}, mie_inspection=#{mie_inspection}, cpsa_supervisor=#{cpsa_supervisor}, `else`=#{else}, state=#{state}, create_by=#{create_by}
where project_id=#{project_id}

update project_before_check
before_check_bill=#{before_check_bill}, issue_list=#{issue_list}, problem_spot=#{problem_spot}, ppss_form=#{ppss_form}, `else`=#{else}, state=#{state}, create_by=#{create_by}
where project_id=#{project_id}

update project_check
cpsp_doc=#{cpsp_doc}, spare_part=#{spare_part}, qa_form=#{qa_form}, problem_form=#{problem_form}, problem_site=#{problem_site}, expiration_begin=#{expiration_begin}, expiration_end=#{expiration_end}, `else`=#{else}, state=#{state}, create_by=#{create_by}
where project_id=#{project_id}

update project_settlement
type_id=#{type_id}, net_number=#{net_number}, contract_amount=#{contract_amount}, pay_state=#{pay_state}, tax_amount=#{tax_amount}, tax_rate=#{tax_rate}, no_tax_amount=#{no_tax_amount}, receipt=#{receipt}, refund=#{refund}, payment=#{payment}, investor=#{investor}, state=#{state}, create_by=#{create_by}
where id=#{id}

update project_settlement

		contract_amount=#{contract_amount}

	where project_id=#{project_id} and type_id=3

mapper.select:
SELECT p.id project_id,p.NAME `name`,t.name type_name,p.location,p.construction_org,p.supervising_org, p.save_state,p.is_urgency,p.state project_state,s.state_name,u.user_name create_by,p.create_at FROM project p LEFT JOIN project_state s ON p.state=s.state_id LEFT JOIN `project_type` t ON p.project_type=t.id left join `user` u on p.create_by=u.id where 1=1
and p.name like CONCAT('%',#{name},'%')
and p.project_type =#{type_id}
and p.state =#{state_id}
order by p.create_at desc

SELECT * FROM (SELECT ps.create_at screate_at,u.user_name screate_by,p.id project_id,p.NAME `name`,t.id project_type,t.name type_name, p.location,p.construction_org,p.supervising_org,p.save_state,p.is_urgency,p.state project_state,s.state_name, p.create_at,p.create_by FROM project p LEFT JOIN project_state s ON p.state=s.state_id LEFT JOIN `project_type` t ON p.project_type=t.id LEFT JOIN `project_settlement` ps ON ps.project_id=p.id LEFT JOIN `user` u ON ps.create_by=u.id
ORDER BY ps.create_at DESC) pp where 1=1
and pp.name like CONCAT('%',#{name},'%')
and pp.project_type =#{type_id}
and pp.project_state =#{state_id}
GROUP BY project_id ORDER BY screate_at DESC

SELECT ps.*,u.user_name,t.type_name FROM `project_settlement` ps INNER JOIN `project_settlement_type` t ON ps.type_id=t.id inner join `user` u on ps.create_by=u.id WHERE 1=1
and ps.project_id=#{project_id}
and ps.state=#{state}
ORDER BY ps.create_at DESC

SELECT p.id project_id,t.name type_name,p.name `name`,location,construction_org,supervising_org,p.supervisor,p.PM,p.PM_phone,ps.create_at start_time,pc.create_at end_time,pc.expiration_end FROM project p INNER JOIN `project_type` t ON t.id=p.project_type INNER JOIN `project_start` ps ON p.id=ps.project_id INNER JOIN `project_check` pc ON p.id=pc.project_id WHERE p.state=7
and p.name like CONCAT('%',#{name},'%')
and p.project_type =#{type_id}
and ps.create_at between #{beginTime} and #{endTime}
order by ps.create_at desc

SELECT SUM(no_tax_amount) no_tax_amount,contract_amount FROM `project_settlement` ps
WHERE ps.state=2

and ps.type_id=#{type_id}


and ps.project_id=#{project_id}
