mapper.select:
SELECT s.id supervision_id,s.project_id,p.name project_name,p.location,t.count,t.type,s.state supervision_state,s.day_count,s.begin_time FROM `supervision` s INNER JOIN project p ON s.project_id=p.id INNER JOIN `supervision_time_type` t ON s.supervision_time_id=t.id WHERE p.state BETWEEN 4 AND 6
and p.name like CONCAT('%',#{name},'%')
and s.supervision_model_id =#{supervision_model_id}
and s.id =#{supervision_id}
order by s.id desc

SELECT si.create_at FROM `supervision_item_relation` si
where 1=1

	and si.supervision_id =#{supervision_id}


	and si.create_at BETWEEN #{startTime} AND #{endTime}

group by si.create_at

SELECT s.*,md.department_id,d.dname FROM `supervision` s LEFT JOIN `supervision_model` m on s.supervision_model_id=m.id LEFT JOIN `supervision_model_department` md on m.id=md.model_id LEFT JOIN `department` d on md.department_id=d.id WHERE 1=1
and s.project_id =#{project_id}
and md.department_id =#{department_id}
ORDER BY s.id DESC

SELECT s.id item_id,s.item_name,s.is_fill,s.is_display FROM `supervision_model_item` s
left join `supervision_model_department` d on s.model_id=d.model_id
WHERE 1=1

		and s.model_id =#{supervision_model_id}


		and d.department_id =#{department_id}

SELECT * FROM `department` d
WHERE 1=1

		and d.id =#{department_id}

SELECT s.id,s.project_id,s.supervision_id,s.item_id,s.content,s.create_at,u.user_name create_by,d.dname FROM `supervision_item_relation` s
LEFT JOIN `user` u ON s.create_by=u.id
LEFT JOIN `department` d ON u.department_id=d.id 
WHERE 1=1

		and s.supervision_id=#{supervision_id}


		and s.project_id=#{project_id}


		and d.id=#{department_id}


		and s.create_at < #{create_at}


		and s.create_at BETWEEN #{beginTime} AND #{endTime}

ORDER BY s.create_at DESC
LIMIT 1

	SELECT * FROM `supervision_item_relation` s
	where 1=1

		and s.supervision_id=#{supervision_id}

  and s.create_at BETWEEN #{beginTime} AND #{endTime}
  group by s.create_at

SELECT s.item_id,i.item_name,i.is_fill,i.is_display,s.content,s.create_at FROM `supervision_item_relation` s
INNER JOIN `supervision_model_item` i ON s.item_id=i.id
WHERE 1=1

	and supervision_id=#{supervision_id}


	and project_id=#{project_id}


	and i.id=#{item_id}


	and s.create_at BETWEEN #{beginTime} AND #{endTime}

ORDER BY s.item_id

SELECT s.item_id,i.item_name,i.is_fill,i.is_display,s.content FROM `supervision_item_relation` s
INNER JOIN `supervision_model_item` i ON s.item_id=i.id
WHERE 1=1 

		and s.supervision_id=#{supervision_id}


		and s.project_id=#{project_id}


		and i.id=#{item_id}


		and s.create_by=#{create_by}


		and s.create_at=#{create_at}


	and s.is_irregular=#{is_irregular}


		and s.create_at between #{beginTime} and #{endTime}

SELECT a.id,a.abarbeitung_id,a.description,a.site_photo,a.report,a.state,a.else,a.create_at,a.create_by,u.user_name FROM `abarbeitung_report` a inner join `user` u on a.create_by=u.id where 1=1
and a.state=2
and a.state=1
and a.state=1 and a.create_by=#{create_by}
and abarbeitung_id=#{abarbeitung_id}

select IFNULL(MAX(c.id),0) as id from construction_item c 
where c.project_id=#{project_id}
and c.id < #{item_id}
order by c.id  limit 1

SELECT s.id schedule_id,s.project_id,p.name project_name,s.construction_organization,s.supervising_organization,s.meeting_data,s.meeting_summary,s.site_photo,s.schedule,s.else,s.create_at,s.create_by FROM `project_schedule` s
INNER JOIN project p ON s.project_id=p.id 
WHERE s.id=#{schedule_id}

SELECT s.id schedule_id,s.meeting_data,s.meeting_summary,s.site_photo,s.schedule,s.else,s.create_at,s.create_by FROM `project_schedule` s
WHERE s.state=2
and s.project_id=#{project_id}

		and s.id < #{schedule_id}

order by s.create_at desc limit 1

mapper.update:
update abarbeitung
project_id=#{project_id}, construction_organization=#{construction_organization}, location=#{location}, time=#{time}, description=#{description}, site_photo=#{site_photo}, harm=#{harm}, punishment=#{punishment}, sign=#{sign}, `else`=#{else}, state=#{state}, create_at=SYSDATE(), create_by=#{create_by}
where id=#{abarbeitung_id}

update abarbeitung_report
description=#{description}, site_photo=#{site_photo}, report=#{report}, `else`=#{else}, state=#{state}, create_at=SYSDATE(), create_by=#{create_by}
where id=#{report_id}

update construction_item
project_id=#{project_id}, construction_organization=#{construction_organization}, type_id=#{type_id}, credential_id=#{credential_id}, photo=#{photo}, item_data=#{item_data}, ossa=#{ossa}, charge=#{charge}, request=#{request}, construction_before=#{construction_before}, special_deal=#{special_deal}, construction_after=#{construction_after}, is_read=#{is_read}, `else`=#{else}, state=#{state}, create_at=SYSDATE(), create_by=#{create_by}
where id=#{item_id}

update `project_schedule`
project_id=#{project_id}, construction_organization=#{construction_organization}, supervising_organization=#{supervising_organization}, meeting_data=#{meeting_data}, meeting_summary=#{meeting_summary}, site_photo=#{site_photo}, schedule=#{schedule}, `else`=#{else}, state=#{state}, create_at=SYSDATE(), create_by=#{create_by}
where id=#{schedule_id}

mapper.select:
SELECT s.id,p.name,t.name type_name,p.location,p.construction_org,p.supervising_org,st.count time_count,st.type time_type,s.begin_time,u.user_name create_by,s.create_at FROM `supervision` s INNER JOIN project p ON s.project_id=p.id INNER JOIN `project_type` t ON p.project_type=t.id left join `user` u on s.create_by=u.id inner join `supervision_time_type` st on s.supervision_time_id=st.id where p.state BETWEEN 4 AND 6
and p.name like CONCAT('%',#{name},'%')
and t.id =#{type_id}
and s.id =#{supervision_id}
and p.construction_org like CONCAT('%',#{construction_org},'%')
order by s.create_at desc

SELECT p.name project_name,pt.name type_name,p.location,p.construction_org,p.supervising_org,c.id item_id,c.project_id,t.id type_id,t.name item_type_name,c.item_data,c.state item_state,u.user_name create_by,c.create_at FROM (SELECT * FROM `construction_item` ORDER BY id DESC ) c INNER JOIN `project` p ON c.project_id=p.id INNER JOIN `project_type` pt ON p.project_type=pt.id INNER JOIN `construction_item_type` t ON c.type_id=t.id INNER JOIN `user` u ON c.create_by =u.id WHERE 1=1
and p.name like CONCAT('%',#{name},'%')
and c.state=2
and c.state=1 and c.create_by=#{create_by}
AND c.item_data=#{item_time}
AND c.type_id=#{type_id}
GROUP BY p.id order by c.create_at desc
